// Parse the CSV string received
// 6852, 0, 6252, 600, 0, 0, 100, 20250619, 932.
// CSV format: 9 comma-separated values
// "powerfromsolar", "powerfromgrid", "powertogrid", "powertohouse", 
// "powertobattery", "powerfrombattery", "batterychargestate", 
// "powerdatestamp", "powertimestamp"
// Note the backquotes ` used.

// Get the payload as CSV string
const csv = msg.payload;
// Split
const parts = csv.trim().split(",");
// Debug
// node.warn(parts);

// Basic validation
if (parts.length !== 9) {
    node.error("Unexpected CSV format", msg);
    return null;
}

// Keys for MQTT topics
// Ensure same as the entity names
const keys = [
    "power_from_solar",
    "power_from_grid",
    "power_to_grid",
    "power_to_house",
    "power_to_battery",
    "power_from_battery",
    "power_battery_charge",
    "power_datestamp",
    "power_timestamp"
];

const topics = [];

for (let i = 0; i < keys.length; i++) {
    const topic = `homeassistant/sensor/${keys[i]}/state`;
    const payload = parts[i];
    topics.push({ topic, payload });
    // node.warn(`${topic}]}`);
    // node.warn(`${payload}`);
}

return [topics];
